    /**
     * @return {string} - the name of this extension.
     */
    static get EXTENSION_NAME () {
        return formatMessage({
          id: 'marty.extension_name',
          default: 'Marty the Robot',
          description: 'Name of the Marty extension'
        });
    }


    /**
     * The ID of the extension.
     * @return {string} the id
     */
    static get EXTENSION_ID () {
        return 'marty';
    }

    static set peripheralFactory (factory) {
        _peripheralFactory = factory;
    };

    constructor (runtime) {

        /**
         * The Scratch 3.0 runtime used to trigger the green flag button.
         * @type {Runtime}
         * @private
         */
        this._runtime = runtime;


        /**
         * Holder for Marty Peripheral Manager instance
         * @type {object}
         * @private
         */
        //this._peripheral = undefined;//new MartyPeripheral(this._runtime, Scratch3MartyBlocks.EXTENSION_ID);
        this._peripheral = _peripheralFactory(this._runtime, Scratch3MartyBlocks.EXTENSION_ID);

        this._blockingMode = true;

        this.jointID = [];
        this.jointID["left hip"] = 0;
        this.jointID["left twist"] = 1;
        this.jointID["left knee"] = 2;
        this.jointID["right hip"] = 3;
        this.jointID["right twist"] = 4;
        this.jointID["right knee"] = 5;
        this.jointID["left arm"] = 6;
        this.jointID["right arm"] = 7;
        this.jointID["eyes"] = 8;
    }

